mapper.update:
UPDATE user_log
user_id = #{userId}, username = #{username}, time = #{time}, ip = #{ip}, country = #{country}, area = #{area}, city = #{city}, org = #{org}, coord = #{coord},
WHERE id = #{id}

mapper.select:
SELECT id, user_id AS userId, username, time, ip, country, area, city, org, coord
FROM user_log
WHERE id = #{id}

SELECT id, user_id AS userId, username, time, ip, country, area, city, org, coord
FROM user_log
WHERE user_id = #{userId}
ORDER BY time DESC
    LIMIT #{start}, #{pageSize}
